import React from "react";
import "./style.css";


const BlogLayout = props => {
    return (
        <div className="blog-layout">
            <div className="row blog-layout">
                <div className="col s4">
                    <img className="blog-image-layout" src="./images/blog-image.jpg" />
                </div>
                <div className="col s8 blog-side-layout">
                    <div className="text-blog-layout">
                        <p className="featured-layout">By Lindsay Mostrom</p>
                        <h1>the best chicken tinga tacos</h1>
                        <p>Literally though, these chicken tinga tacos are the best.
Do you have 20 minutes? Cool. You can make these because this is not fancy. This is just really, really delicious chicken tinga tacos.</p>
                        <br></br>
                    </div>
                </div>
            </div>
        </div>

    );
}

export default BlogLayout;
